import React, { Component } from "react";

import ScrollTop from "react-scrolltop-button";
import { SRLWrapper } from "simple-react-lightbox"; // Import SRLWrapper
import SimpleReactLightbox from "simple-react-lightbox"; // Import Simple React Lightbox
import axios from "axios";
import Lightbox from "react-image-lightbox";
import "react-image-lightbox/style.css";
import { makeStyles } from "@material-ui/core/styles";
import GridList from "@material-ui/core/GridList";
import GridListTile from "@material-ui/core/GridListTile";
import GridListTileBar from "@material-ui/core/GridListTileBar";
import IconButton from "@material-ui/core/IconButton";
import FullscreenIcon from "@material-ui/icons/Fullscreen";
import Modal from "@material-ui/core/Modal";

function rand() {
  return Math.round(Math.random() * 20) - 10;
}

function getModalStyle() {
  const top = 50 + rand();
  const left = 50 + rand();

  return {
    top: `${top}%`,
    left: `${left}%`,
    transform: `translate(-${top}%, -${left}%)`,
  };
}

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "space-around",
    overflow: "hidden",
    "margin-top": "10%",
    backgroundColor: theme.palette.background.paper,
  },
  gridList: {
    width: 1200,
    height: 650,
    // Promote the list into his own layer on Chrome. This cost memory but helps keeping high FPS.
    transform: "translateZ(0)",
  },
  titleBar: {
    background:
      "linear-gradient(to top, rgba(0,0,0,0.7) 0%, rgba(0,0,0,0.3) 70%, rgba(0,0,0,0) 100%)",
  },
  icon: {
    color: "white",
  },
  paper: {
    position: "relative",
    width: 500,
    backgroundColor: theme.palette.background.paper,
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
  },
}));
export default class MovieGallery extends Component {
  state = {
    movieURL1: "",
    movieURL2: "",
    movieURL3: "",
    movieURL4: "",
    movieURL5: "",
    movieURL6: "",
    movieURL7: "",
    movieURL8: "",
    movies: [
      {
        IMBD_ID: "tt6751668",
        title: "",
        director: "",
        rating: "",
        posterURL: "",
      },
      {
        IMBD_ID: "tt0816692",
        title: "",
        director: "",
        rating: "",
        posterURL: "",
      },
      {
        IMBD_ID: "tt1457767",
        title: "",
        director: "",
        rating: "",
        posterURL: "",
      },
      {
        IMBD_ID: "tt0499549",
        title: "",
        director: "",
        rating: "",
        posterURL: "",
      },
      {
        IMBD_ID: "tt4154796",
        title: "",
        director: "",
        rating: "",
        posterURL: "",
      },
      {
        IMBD_ID: "tt5700672",
        title: "",
        director: "",
        rating: "",
        posterURL: "",
      },
      {
        IMBD_ID: "tt10039344",
        title: "",
        director: "",
        rating: "",
        posterURL: "",
      },
      {
        IMBD_ID: "tt0198781",
        title: "",
        director: "",
        rating: "",
        posterURL: "",
      },
    ],
    photoIndex: 0,
    isOpen: false,
  };

  constructor(props) {
    super(props);
    // TODO
    var movieURLs = ["", "", "", "", "", "", "", ""];
    var movieArray = [
      {
        IMBD_ID: "tt6751668",
        title: "",
        director: "",
        rating: "",
        posterURL: "",
      }, //The Wandering Earth
      {
        IMBD_ID: "tt4154796",
        title: "",
        director: "",
        rating: "",
        posterURL: "",
      }, //Interstellar
      {
        IMBD_ID: "tt0111161",
        title: "",
        director: "",
        rating: "",
        posterURL: "",
      }, //The Conjuring
      {
        IMBD_ID: "tt0816692",
        title: "",
        director: "",
        rating: "",
        posterURL: "",
      }, //Avatar
      {
        IMBD_ID: "tt1375666",
        title: "",
        director: "",
        rating: "",
        posterURL: "",
      }, //Avengers: Endgame
      {
        IMBD_ID: "tt0118799",
        title: "",
        director: "",
        rating: "",
        posterURL: "",
      }, //Train to Busan
      {
        IMBD_ID: "tt7286456",
        title: "",
        director: "",
        rating: "",
        posterURL: "",
      }, //Countdown
      {
        IMBD_ID: "tt0298203",
        title: "",
        director: "",
        rating: "",
        posterURL: "",
      }, //Monsters, Inc.
    ];

    const axios = require("axios").default; // axios.<method> will now provide autocomplete and parameter typings

    movieArray.forEach((item, index) => {
      let url = "https://www.omdbapi.com/?apikey=cab6a2b3&i=" + item.IMBD_ID;
      let recvPack = axios.get(url);
      //console.log(index);
      //recvPack
      axios
        .get(url)
        .then((response) => {
          const packData = response.data;
          const recvTitle = packData.Title;
          const recvDirector = packData.Director;
          const recvRating = packData.imdbRating;
          const recvPosterURL = packData.Poster;
          movieArray[index].title = recvTitle;
          movieArray[index].director = recvDirector;
          movieArray[index].rating = recvRating;
          movieArray[index].posterURL = recvPosterURL;
          movieURLs[index] = recvPosterURL;
          console.log(movieURLs[index]);
          //movieArray[index].title = packData.data.Title;                  //new value
        })
        .then(() => {
          console.log("movieURL is: " + movieURLs[0]);
          this.state.movieURL1 = movieURLs[0];
          this.state.movies = movieArray;
          this.forceUpdate();
        });
    });
    console.log(movieArray);
  }

  componentWillMount() {
    document.title = "My web"; //define page name

    //console.log(movieURLs[0])
    //this.state.movies = movieArray;
    //console.log(movieURLs[1])

    //console.log(this.state.movies)
    //console.log(this.state.movieURL1)
  }
  render() {
    const { photoIndex, isOpen } = this.state;
    var movieURLs = this.props.movieURLs;
    return (
      <div>
        <button type="button" onClick={() => this.setState({ isOpen: true })}>
          Open Lightbox
        </button>

        {isOpen && (
          <Lightbox
            mainSrc={movieURLs[photoIndex]}
            nextSrc={movieURLs[(photoIndex + 1) % movieURLs.length]}
            prevSrc={
              movieURLs[(photoIndex + movieURLs.length - 1) % movieURLs.length]
            }
            onCloseRequest={() => this.setState({ isOpen: false })}
            onMovePrevRequest={() =>
              this.setState({
                photoIndex:
                  (photoIndex + movieURLs.length - 1) % movieURLs.length,
              })
            }
            onMoveNextRequest={() =>
              this.setState({
                photoIndex: (photoIndex + 1) % movieURLs.length,
              })
            }
          />
        )}
      </div>
    );
  }
}
