import React, { Component } from "react";

import ScrollTop from "react-scrolltop-button";
import { SRLWrapper } from "simple-react-lightbox"; // Import SRLWrapper
import SimpleReactLightbox from "simple-react-lightbox"; // Import Simple React Lightbox
import axios from "axios";
import Lightbox from "react-image-lightbox";
import "react-image-lightbox/style.css";
import { makeStyles } from "@material-ui/core/styles";
import Movie from "./movie.jsx";
import GridList from "@material-ui/core/GridList";
import Modal from "@material-ui/core/Modal";

function rand() {
  return Math.round(Math.random() * 20) - 10;
}
function rootStyle() {
  return {
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "space-around",
    overflow: "hidden",
  };
}
function GridListStyle() {
  return {
    position: "absolute",
    paddingTop: "50px",
    paddingBottom: "50px",
    backgroundColor: "#2e2e2ebd",
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(200px, 1fr))",
    gridGap: "1rem",
    width: "100%",
  };
}
function getModalStyle() {
  const top = 50 + rand();
  const left = 50 + rand();

  return {
    top: `${top}%`,
    left: `${left}%`,
    transform: `translate(-${top}%, -${left}%)`,
  };
}

const movies = [
  {
    key: "0",
    id: "tt0990372",
  },
  {
    key: "1",
    id: "tt6751668",
  },
  {
    key: "2",
    id: "tt0478087",
  },
  {
    key: "3",
    id: "tt7016936",
  },
  {
    key: "4",
    id: "tt0944947",
  },
  {
    key: "5",
    id: "tt6966692",
  },
  {
    key: "6",
    id: "tt0245429",
  },
  {
    key: "7",
    id: "tt5311514",
  },
];
const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "space-around",
    overflow: "hidden",
    "margin-top": "10%",
    backgroundColor: theme.palette.background.paper,
  },
  gridList: {
    width: 1200,
    height: 650,
    // Promote the list into his own layer on Chrome. This cost memory but helps keeping high FPS.
    transform: "translateZ(0)",
  },
  titleBar: {
    background:
      "linear-gradient(to top, rgba(0,0,0,0.7) 0%, rgba(0,0,0,0.3) 70%, rgba(0,0,0,0) 100%)",
  },
  icon: {
    color: "white",
  },
  paper: {
    position: "relative",
    width: 500,
    backgroundColor: theme.palette.background.paper,
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
  },
}));

export default class MovieGallery extends Component {
  constructor() {
    super();
    this.state = {
      moviePoster: "",
      open: false,
      key: 1,
    };
    this.changeMoviePoster = (data) => {
      this.setState({
        moviePoster: data,
      });
    };
    this.changeKey = (k) => {
      this.setState({
        key: k,
      });
    };
    this.changeOpen = (boo) => {
      this.setState({
        open: boo,
      });
    };
  }

  render() {
    return (
      <div style={rootStyle()}>
        <GridList cellHeight={150} spacing={1} style={GridListStyle()}>
          {Object.values(movies).map((movie) => (
            <Movie
              movieid={movie.id}
              indexKey={movie.key}
              changeIndexKey={this.changeKey}
              changeOpen={this.changeOpen}
              changeMoviePoster={this.changeMoviePoster}
            />
          ))}
        </GridList>
        {/* {console.log(this.state.moviePoster.Poster)} */}
        <Modal
          open={this.state.open}
          onClose={() => {
            this.changeOpen(false);
          }}
          aria-labelledby="simple-modal-title"
          aria-describedby="simple-modal-description"
        >
          {
            <div style={getModalStyle()}>
              <img src={this.state.moviePoster.Poster} alt={"poster"} />
              <div>
                <p>
                  Title: {this.state.moviePoster.Title} <br />
                  Director: {this.state.moviePoster.Direcotr} <br />
                  Genre: {this.state.moviePoster.Genre}
                  <br />
                  imdbRating: {this.state.moviePoster.imdbRating}
                </p>
              </div>
            </div>
          }
        </Modal>
      </div>
    );
  }
}
