      },
    ],
    photoIndex: 0,
    isOpen: false,
  };

  constructor(props) {
    super(props);
    // TODO
    var movieURLs = ["", "", "", "", "", "", "", ""];
    var movieArray = [
      {
        IMBD_ID: "tt6751668",
        title: "",
        director: "",
        rating: "",
        posterURL: "",
        id: 0,
      }, //The Wandering Earth
      {
        IMBD_ID: "tt4154796",
        title: "",
