        id: 7,
      }, //Monsters, Inc.
    ];
    function idtosrc(id) {
      var src = "";
      switch (id) {
        case 1:
          src = this.props.movieURL2;
          break;
        case 2:
          src = this.props.movieURL3;
          break;
        case 3:
          src = this.props.movieURL4;
          break;
        case 4:
          src = this.props.movieURL5;
          break;
        case 5:
          src = this.props.movieURL6;
          break;
        case 6:
          src = this.props.movieURL7;
