  }
  render() {
    var m = this.props.movieArray;
    return (
      <div className={this.props.classes.root}>
        <GridList
          cellHeight={310}
          spacing={0.5}
          cols={4}
          rows={3}
          className={this.props.classes.gridList}
        >
          {m.map((tile) => (
            <GridListTile key={tile.title} cols={2} rows={2}>
              <img src={tile.posterURL} />
              <GridListTileBar
                title={tile}
                titlePosition="bottom"
                actionIcon={
                  <IconButton
                    aria-label={`full ${tile.title}`}
                    className={this.props.classes.icon}
                    onClick={() => {
                      this.props.setImgKey(tile.id);
                      this.props.handleOpen();
