          aria-labelledby="simple-modal-title"
          aria-describedby="simple-modal-description"
        >
          {
            <div
              style={this.props.modalStyle}
              className={this.props.classes.paper}
            >
              <img src={this.props.idtosrc(imgKey)} class="modal-content" />
            </div>
          }
        </Modal>
      </div>
    );
  }
}
