        director: "",
        rating: "",
        posterURL: "",
      },
      {
        IMBD_ID: "tt0198781",
        title: "",
        director: "",
        rating: "",
        posterURL: "",
      },
    ],
  };

  constructor(props) {
    super(props);
    // TODO
    var movieURLs = ["", "", "", "", "", "", "", ""];
    var movieArray = [
      {
        IMBD_ID: "tt6751668",
        title: "",
        director: "",
        rating: "",
        posterURL: "",
      }, //The Wandering Earth
      {
        IMBD_ID: "tt4154796",
        title: "",
        director: "",
        rating: "",
        posterURL: "",
      }, //Interstellar
      {
        IMBD_ID: "tt0111161",
        title: "",
        director: "",
        rating: "",
        posterURL: "",
      }, //The Conjuring
      {
        IMBD_ID: "tt0816692",
        title: "",
        director: "",
        rating: "",
        posterURL: "",
      }, //Avatar
      {
        IMBD_ID: "tt1375666",
        title: "",
        director: "",
        rating: "",
        posterURL: "",
      }, //Avengers: Endgame
      {
        IMBD_ID: "tt0118799",
        title: "",
        director: "",
        rating: "",
        posterURL: "",
      }, //Train to Busan
      {
        IMBD_ID: "tt7286456",
        title: "",
        director: "",
        rating: "",
        posterURL: "",
      }, //Countdown
      {
        IMBD_ID: "tt0298203",
        title: "",
        director: "",
        rating: "",
        posterURL: "",
      }, //Monsters, Inc.
    ];

    const axios = require("axios").default; // axios.<method> will now provide autocomplete and parameter typings

    movieArray.forEach((item, index) => {
      let url = "https://www.omdbapi.com/?apikey=cab6a2b3&i=" + item.IMBD_ID;
      let recvPack = axios.get(url);
      //console.log(index);
      //recvPack
      axios
        .get(url)
        .then((response) => {
          const packData = response.data;
          const recvTitle = packData.Title;
          const recvDirector = packData.Director;
          const recvRating = packData.imdbRating;
          const recvPosterURL = packData.Poster;
          movieArray[index].title = recvTitle;
          movieArray[index].director = recvDirector;
          movieArray[index].rating = recvRating;
          movieArray[index].posterURL = recvPosterURL;
          movieURLs[index] = recvPosterURL;
          //console.log(movieURLs[index])
          //movieArray[index].title = packData.data.Title;                  //new value
        })
        .then(() => {
          console.log("movieURL is: " + movieURLs[0]);
          this.state.movieURL1 = movieURLs[0];
          this.state.movies = movieArray;
          this.forceUpdate();
        });
    });
    console.log(movieArray);
  }

  componentWillMount() {
    document.title = "My web"; //define page name

    //console.log(movieURLs[0])
    //this.state.movies = movieArray;
    //console.log(movieURLs[1])

    //console.log(this.state.movies)
    //console.log(this.state.movieURL1)
  }
  render() {
    return (
      <body>
        <div className="row">
          <div className="column">
            <img
              src={this.state.movies[0]["posterURL"]}
              style={{ width: "100%" }}
              onClick={() => {
                slide.openModal();
                slide.currentSlide(1);
              }}
              className="hover-shadow cursor"
            />
          </div>
          <div className="column">
            <img
              src={this.state.movies[1]["posterURL"]}
              style={{ width: "100%" }}
              onClick={() => {
                slide.openModal();
                slide.currentSlide(2);
              }}
              className="hover-shadow cursor"
            />
          </div>
          <div className="column">
            <img
              src={this.state.movies[2]["posterURL"]}
              style={{ width: "100%" }}
              onClick={() => {
                slide.openModal();
                slide.currentSlide(3);
              }}
              className="hover-shadow cursor"
            />
          </div>
          <div className="column">
            <img
              src={this.state.movies[3]["posterURL"]}
              style={{ width: "100%" }}
              onClick={() => {
                slide.openModal();
                slide.currentSlide(4);
              }}
              className="hover-shadow cursor"
            />
          </div>
        </div>

        <div id="myModal" className="modal">
          <span className="close cursor" onclick="closeModal()">
            &times;
          </span>
          <div className="modal-content">
            <div className="mySlides">
              <div className="numbertext">1 / 4</div>
              <img
                src={this.state.movies[0]["posterURL"]}
                style={{ width: "100%" }}
              />
            </div>

            <div className="mySlides">
              <div className="numbertext">2 / 4</div>
              <img
                src={this.state.movies[1]["posterURL"]}
                style={{ width: "100%" }}
              />
            </div>

            <div className="mySlides">
              <div className="numbertext">3 / 4</div>
              <img
                src={this.state.movies[2]["posterURL"]}
                style={{ width: "100%" }}
              />
            </div>

            <div className="mySlides">
              <div className="numbertext">4 / 4</div>
              <img
                src={this.state.movies[3]["posterURL"]}
                style={{ width: "100%" }}
              />
            </div>

            <a className="prev" onClick={() => slide.plusSlides(-1)}>
              &#10094;
            </a>
            <a className="next" onClick={() => slide.plusSlides(1)}>
              &#10095;
            </a>

            <div className="caption-container">
              <p id="caption"></p>
            </div>

            <div className="column">
              <img
                className="demo cursor"
                src={this.state.movies[0]["posterURL"]}
                style={{ width: "100%" }}
                onClick={() => slide.currentSlide(1)}
                alt="Nature and sunrise"
              />
            </div>
            <div className="column">
              <img
                className="demo cursor"
                src={this.state.movies[1]["posterURL"]}
                style={{ width: "100%" }}
                onClick={() => slide.currentSlide(2)}
                alt="Snow"
              />
            </div>
            <div className="column">
              <img
                className="demo cursor"
                src={this.state.movies[2]["posterURL"]}
                style={{ width: "100%" }}
                onClick={() => slide.currentSlide(3)}
                alt="Mountains and fjords"
              />
            </div>
            <div className="column">
              <img
                className="demo cursor"
                src={this.state.movies[3]["posterURL"]}
                style={{ width: "100%" }}
                onClick={() => slide.currentSlide(4)}
                alt="Northern Lights"
              />
            </div>
          </div>
        </div>
      </body>
    );
  }
}
